const { combineRgb } = require('@companion-module/base');
const actionDef =  {

	goto : {
		name: 'Goto Desktop',
		options:  [
			{
				id: 'index',
				type: 'number',
				label: 'Desktop Index',
				default: 0,
				min: 0,
				max: 10
			},

			{
				id: 'name',
				type: 'textinput',
				label: 'Desktop Name',
				default: '',
			}
		],
		callback: async (event) => {

			actionDef.api.goto(event.options.name || event.options.index).then(function(info){
				actionDef.api.setVariableValues({visible : info.name,visibleIndex :info.index });
			});
		},
	},

	

	presets : {

		"goto": {
			category: 'Desktop',
			"type": "button",
			"style": {
				"text": "1\\n",
				"size": "24",
				"png": null,
				"alignment": "center:center",
				"pngalignment": "center:center",
				"color": 16777215,
				"bgcolor": 0,
				"show_topbar": false,
				"png64": ""
			},
			"options": {
				"relativeDelay": false,
				"stepAutoProgress": true
			},
			"feedbacks": [],
			steps: [
				{
					down: [
						{
							actionId: 'goto',
							options: {
								index : 0			 
							},
						},
					],
					up: [],
				}],
		}

	}

};

module.exports = function (self) {
	actionDef.api = self.api;
	actionDef.ready = new Promise(function(resolve){
		self.api.count().then(updatePresets);


		function updatePresets(count) {
			if (count<=0) return;
	
			delete actionDef.presets.goto;
	
			for (let i = 0;i < count; i ++ ) {
				actionDef.presets[`goto${i}`] =  {
					category: 'Desktop',
					"type": "button",
					"style": {
						"text": (i+1).toString()+"\\n",
						"size": "24",
						"png": null,
						"alignment": "center:center",
						"pngalignment": "center:center",
						"color": 16777215,
						"bgcolor": 0,
						"show_topbar": false,
						"png64": ""
					},
					"options": {
						"relativeDelay": false,
						"stepAutoProgress": true
					},
					"feedbacks": [],
					steps: [
						{
							down: [
								{
									actionId: 'goto',
									options: {
										index : i			 
									},
								},
							],
							up: [],
						}],
				};
		
			}

			resolve(true);
		}
	});
	
	 
	return actionDef;

	
};

